// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract SudokuArena {
    enum State {
        NONE,
        CREATED,
        ACCEPTED,
        IN_PROGRESS,
        SUBMISSION_OPEN,
        FINALIZED,
        REFUNDED,
        CANCELED
    }

    struct Match {
        address creator;
        address opponent;
        bool isPvp;
        State state;
        uint64 createdAt;
        uint64 acceptBy;
        uint64 resolveBy;
        uint32 gameId;
        uint256 startBlock;
        uint256 creatorFinishBlock;
        uint256 opponentFinishBlock;
        uint8 puzzleIndex;
        bytes32 puzzleHash;
        bytes packedGivens;
    }

    uint256 public nextMatchId;
    mapping(uint256 => Match) public matches;

    event MatchCreated(
        uint256 indexed matchId,
        address indexed creator,
        address indexed opponent,
        bool isPvp,
        uint32 gameId,
        uint64 acceptBy,
        uint64 resolveBy,
        uint8 puzzleIndex,
        bytes32 puzzleHash
    );
    event MatchAccepted(uint256 indexed matchId, address indexed opponent);
    event MatchStateUpdated(uint256 indexed matchId, State state);
    event MoveSubmitted(uint256 indexed matchId, address indexed player, uint8 row, uint8 col, uint8 value);

    function createPveMatch(
        uint32 gameId,
        uint8 puzzleIndex,
        bytes32 puzzleHash,
        bytes calldata packedGivens
    ) external returns (uint256 matchId) {
        matchId = ++nextMatchId;
        matches[matchId] = Match({
            creator: msg.sender,
            opponent: address(0),
            isPvp: false,
            state: State.CREATED,
            createdAt: uint64(block.timestamp),
            acceptBy: 0,
            resolveBy: 0,
            gameId: gameId,
            startBlock: 0,
            creatorFinishBlock: 0,
            opponentFinishBlock: 0,
            puzzleIndex: puzzleIndex,
            puzzleHash: puzzleHash,
            packedGivens: packedGivens
        });

        emit MatchCreated(
            matchId,
            msg.sender,
            address(0),
            false,
            gameId,
            0,
            0,
            puzzleIndex,
            puzzleHash
        );
    }

    function createPvpMatch(
        address opponent,
        uint32 gameId,
        uint64 acceptBy,
        uint64 resolveBy,
        uint8 puzzleIndex,
        bytes32 puzzleHash,
        bytes calldata packedGivens
    ) external returns (uint256 matchId) {
        require(opponent != address(0), "SudokuArena: opponent is zero");
        require(acceptBy > block.timestamp, "SudokuArena: acceptBy in past");
        require(resolveBy > acceptBy, "SudokuArena: resolveBy too early");

        matchId = ++nextMatchId;
        matches[matchId] = Match({
            creator: msg.sender,
            opponent: opponent,
            isPvp: true,
            state: State.CREATED,
            createdAt: uint64(block.timestamp),
            acceptBy: acceptBy,
            resolveBy: resolveBy,
            gameId: gameId,
            startBlock: 0,
            creatorFinishBlock: 0,
            opponentFinishBlock: 0,
            puzzleIndex: puzzleIndex,
            puzzleHash: puzzleHash,
            packedGivens: packedGivens
        });

        emit MatchCreated(
            matchId,
            msg.sender,
            opponent,
            true,
            gameId,
            acceptBy,
            resolveBy,
            puzzleIndex,
            puzzleHash
        );
    }

    function acceptMatch(uint256 matchId) external {
        Match storage m = matches[matchId];
        require(m.state == State.CREATED, "SudokuArena: not created");
        require(m.isPvp, "SudokuArena: not pvp");
        require(msg.sender == m.opponent, "SudokuArena: not opponent");
        require(block.timestamp < m.acceptBy, "SudokuArena: accept expired");

        m.state = State.ACCEPTED;
        emit MatchAccepted(matchId, msg.sender);
        emit MatchStateUpdated(matchId, State.ACCEPTED);
    }
}

